def how_much(journalist_table)
    nb_journalist = journalist_table.length
    puts "Le nombre de journaliste présent dans la data est de #{nb_journalist}"
end

def shorter(journalist_table)
    name_shorter = journalist_table.min{|a, b| a.size <=> b.size}
    puts "Le nom du journaliste sur Twitter le plus court est #{name_shorter}"
end

def have_five_string(journalist_table)
    egal_five = 0
    journalist_table.each do |name|
        if name.length == 6 
            egal_five += 1
        end
    end
    puts "Le nombre de nom égaux à 5 lettres sont de #{egal_five}"
end

def first_uppercase(journalist_table)
    count_upcase = 0
    journalist_table.each do |i|
        if i[1] == i[1].upcase
            count_upcase += 1
        end
    end
    puts "Le nombre de handle commençant par une majuscule sont de #{count_upcase}"
end

def ascending_sort(journalist_table)
    new_journalist_table = journalist_table.sort{|a, b| a <=> b}
    #puts new_journalist_table
end

def size_sort(journalist_table)
    little_to_big = journalist_table.sort{|a, b| a.length <=> b.length}
    #puts little_to_big
end

def science_pos(journalist_table)
    puts "L'index de epenser est le numéro #{journalist_table.index("@epenser")}"
end

def bool_verif_length(journalist_table, i)
    frequence = 0
    journalist_table.each do |f|
        if f.length == i 
            frequence += 1
        end
    end
    if frequence != 0
        puts "Il y a #{frequence} handles avec #{i} caractères"
    end
end

def frequences(journalist_table)
    longuest = journalist_table.max{|a, b| a.length <=> b.length}
    (longuest.length+1).times do |i|
        bool_verif_length(journalist_table, i)
    end
end


def perform
    journalist_table = journalist_data
    how_much(journalist_table)
    shorter(journalist_table)
    have_five_string(journalist_table)
    first_uppercase(journalist_table)
    ascending_sort(journalist_table)
    size_sort(journalist_table)
    science_pos(journalist_table)
    frequences(journalist_table)
end

